package com.syntax.class08;

public class ContinuePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	for (int i=1; i<10; i++) {
		if (i==5 || i==6 || i==7) {
			continue; // continue skips the if code and print the rest
		}
		System.out.println(i);
	}
	}

}
